// Media breakpoints 
 @mixin breakpoint($point) {
  @if $point == desktop {
     @media screen and (min-device-width: 1240px)  { @content ; }
     @media screen and (min-width: 1240px) { @content ; }
  }
  @else if $point == huge {
    @media screen and (max-device-width: 1577px)  { @content ; }
    @media screen and (max-width: 1577px) { @content ;}
  }
  @else if $point == laptop {
     @media screen and (max-device-width: 1239px)  { @content ; }
     @media screen and (max-width: 1239px) { @content ; }
  }
  @else if $point == tablet {
     @media screen and (max-device-width: 991px)  { @content ; }
     @media screen and (max-width: 991px) { @content ; }
  }
  @else if $point == smallest {
     @media screen and (max-width: 768px)  { @content ; }
  }
  @else if $point == formobile {
      @media screen and (max-device-width: 508px)  { @content ; }
  }
  @else if $point == smallheightfix {
      @media screen and (max-height: 750px) and (min-width: 977px) { @content ; }
  }
}

html, body {
    height: 100%;
}

.big-container {
    flex: 1;
}

#app {
    display: flex;
    flex-direction: column;
    position: relative;
    min-height: 100vh;
    overflow: hidden;
    padding-bottom: 9.5rem;

    .main-about-text {
        font-size: 3rem;
        padding-bottom: 1.8rem;

        @include breakpoint(formobile) {
            font-size: 2.3rem;
        }
    }

    .video-header {
        color: white;
    }
}

/////////////////////
// HEADER + FOOTER //
/////////////////////

#app .navbar, #footer {
    border-radius: 0;
    background: linear-gradient(180deg, #2b77c4 0%, #2a5a8a 100%);
    z-index: 10;

    img {
        max-width: 400px;
        margin-left: -2.5rem;
        margin-right: -2.5rem;

        @include breakpoint(smallest) {
            max-width: 300px;
        }
    }

    a {
        color: white;
    }

    > .menu {
        border: none;
        display: flex;
        justify-content: center;
        align-items: center;
    }

    .header {
        text-align: center;
        color: white;
        margin-top: 0;
        font-weight: 100;
        font-size: 1.5rem;
    }

    .item > .content {
        padding: 2rem;

        @include breakpoint(laptop) {
            padding: 0rem;
            padding-bottom: 1rem;
        }
    }

    .header-burger {
        display: none;

        @include breakpoint(laptop) {
            display: block;
        }


        .item {
            font-size: 1.5rem;
            color: #2a5a8a;
            padding: 2rem;

            @include breakpoint(formobile) {
                font-size: 1.2rem;
                padding: 1.5rem;
            }
        }

        .item:hover {
            background: linear-gradient(180deg, #2b77c4 0%, #2a5a8a 100%);
            color: white;
            transition: .1s;
        }
    }

    div.header-burger {
        position: absolute;
        right: 1rem;

        .menu {
            left: -11.3rem;
            border: none;

            @include breakpoint(formobile) {
                left: -8.3rem;
            };
        }

        i.icon {
            font-size: 2rem;
