// Media breakpoints 
 @mixin breakpoint($point) {
  @if $point == desktop {
     @media (min-width: 1240px) { @content ; }
  }
  @else if $point == laptop {
     @media (max-width: 1239px) { @content ; }
  }
  @else if $point == tablet {
     @media (max-width: 991px) { @content ; }
  }
  @else if $point == mobileonly {
     @media (max-width: 768px)  { @content ; }
  }
}

#app {
    .main-about-text {
        font-size: 3rem;
        padding-bottom: 1.8rem;
    }

    .video-header {
        color: white;
    }
}

// HEADER + FOOTER

#app .navbar, #footer {
    border-radius: 0;
    background: #2a5a8a;
    z-index: 10;

    img {
        max-width: 400px;

        @include breakpoint(mobileonly) {
            max-width: 300px;
        }
    }

    a {
        color: white;
        padding-left: 1rem;
        padding-right: 1rem;
    }

    > .menu {
        border: none;
        display: flex;
        justify-content: center;
        align-items: center;
    }

    .header {
        text-align: center;
        color: white;
        margin-top: 0;
        font-weight: 100;
        font-size: 1.5rem;
    }

    .item > .content {
        padding: 2rem;

        @include breakpoint(tablet) {
            padding: 0rem;
            padding-bottom: 1rem;
        }
    }

    .header-burger {
        display: none;

        @include breakpoint(tablet) {
            display: block;
        }

        .item {
            font-size: 1.5rem;
            color: #2a5a8a;
            padding: 2rem;
        }

        .item:hover {
            background: #2a5a8a;
            color: white;
            transition: .3s;
        }
    }

    div.header-burger {
        position: absolute;
        right: 1rem;

        .menu {
            left: -11.3rem;
            border: none;
        }

        i.icon {
            font-size: 2rem;
        }
    }

    .hide-header {
        display: flex;
        align-items: center;
        font-size: 1.2rem;

        @include breakpoint(tablet) {
            display: none;
        }
        image {
            @include breakpoint(tablet) {
                display: inline-flex;
            }
        }
    }
}

#footer {
    margin-top: 0;
}

// HOMEPAGE

#main-background {
    background: url('/img/background1.jpg');
    height: 70vh;
    background-size: cover;
    background-position: center;
    background-position-x: -17rem;

    @include breakpoint(tablet) {
        height: 100%;
        background-position-x: 15rem;
     }

    .main-about-text {
        @include breakpoint(tablet) {
            padding: 5rem;
            background: #ffffffbd;
        }
        padding: 8rem;
        padding-top: 11rem;

        .header {
            font-size: 2.9rem;
            line-height: 3.5rem;
            widows: 2;
        }
        p {
            font-size: 1.3rem;
        }
    }

    #about-me-front {
        font-size: 3rem;
        font-weight: 900;
    }
}

#tutorial-videos {
    padding-left: 10rem;
    padding-right: 10rem;
    padding-bottom: 8rem;
    padding-top: 6rem;
    display: block;
    text-align: center;
    background: #2a5a8a;
    @include breakpoint(tablet) {
        padding: 4rem;
    }
}

#testimonials {
    padding-left: 10rem;
    padding-right: 10rem;
    padding-bottom: 8rem;
    padding-top: 6rem;
    display: block;
    text-align: center;
    background: url('/img/testimonial-background3.jpg');
    background-size: cover;
    background-position: center;

    @include breakpoint(tablet) {
        padding: 4rem;
    }

    .ui.fluid.card {
        box-shadow: none;
        -webkit-box-shadow: none;
        background: transparent;
    }

    .testimonials-text {
        min-height: 231px;
    }
}

// BOOKING LESSONS

#book-lesson {
    min-height: 78vh;
    margin-top: -6.3rem;

    @include breakpoint(mobileonly) {
        margin-top: -1.9rem;
    }

    .sixteen.wide.column {
        padding-bottom: 6rem;
    }
}

// ABOUT
#about-wrapper {
    .about-vid {
        padding: 6rem;

        @include breakpoint(mobileonly) {
            padding: 3rem;
        }
    }
}

#about{
    border: 58px solid #2a5a8a24;

    @include breakpoint(mobileonly) {
        border: 44px solid #2a5a8a24;
    }

    .header {
        text-align: center;
        font-size: 2.9rem;
        flex-grow: 0;
        -webkit-box-flex: 0;
        -ms-flex-positive: 0;
    }

    .about-text {
        padding: 4rem;

        @include breakpoint(mobileonly) {
            padding: 3rem;
        }
    }

    .column.about-img {
        padding: 0rem;
    }

    .about-text, .about-img {
        box-shadow: 0px -1px 23px #2a5a89;
        border: 36px inset #2a5a89;

        @include breakpoint(laptop) {
            width: 100% !important;
        }
    }

    #about-card {
        width: 100%;
        padding: 0rem;

        @include breakpoint(laptop) {
            width: 93%;
            display: inline-flex;
        }

        @include breakpoint(mobileonly) {
            width: 100%;
            display: block;
        }
    }

    p:nth-child(2) {
        font-size: 1.3rem;
        font-weight: 900;
    }

    img {
        // padding: 1rem;
        width: 100%;
    }

    p {
        font-size: 1.1rem;
        flex-grow: 0;
        -webkit-box-flex: 0;
        -ms-flex-positive: 0;
    }
}

// CONTACT

#contact-wrapper {

    iframe {
        width: 100%;
        height: 73vh;

        @include breakpoint(mobileonly) {
            height: 81vh;
        }
    }

    #contact-form {
        display: block;
        padding: 6rem;
        padding-top: 2rem;
    }

    .header {
        text-align: center;
        font-size: 2.9rem;
        padding-top: 4rem;
        padding-bottom: 0;
    }

    .field {
        padding-top: 1rem;
    }
}

// RESPONSIVE IFRAME

.iframe-container {
  overflow: hidden;
  padding-top: 56.25%;
  position: relative;

  @include breakpoint(tablet) {
    width: 80%;
    margin: 0 auto;
  }

  @include breakpoint(mobileonly) {
    width: 100%;
  }

  iframe {
    position: absolute;
    top: 0;
    left: 0;
    border: 0;
    width: 100%;
    height: 100%;
  }
} 

// HELPER CSS

#main-background {
    .tablet-display-none {
        @include breakpoint(tablet) {
            display: none;
        }
    }
}
