// Media breakpoints 
 @mixin breakpoint($point) {
  @if $point == desktop {
     @media screen and (min-device-width: 1240px)  { @content ; }
     @media screen and (min-width: 1240px) { @content ; }
  }
  @else if $point == huge {
    @media screen and (max-device-width: 1577px)  { @content ; }
    @media screen and (max-width: 1577px) { @content ;}
  }
  @else if $point == laptop {
     @media screen and (max-device-width: 1239px)  { @content ; }
     @media screen and (max-width: 1239px) { @content ; }
  }
  @else if $point == tablet {
     @media screen and (max-device-width: 991px)  { @content ; }
     @media screen and (max-width: 991px) { @content ; }
  }
  @else if $point == smallest {
     @media screen and (max-width: 768px)  { @content ; }
  }
  @else if $point == formobile {
      @media screen and (max-device-width: 508px)  { @content ; }
  }
}

html {
    height: 100%;
}

.big-container {
    flex: 1;
}

#app {
    display: flex;
    flex-direction: column;
    min-height: 100vh;
    overflow: hidden;

    .main-about-text {
        font-size: 3rem;
        padding-bottom: 1.8rem;

        @include breakpoint(formobile) {
            font-size: 2.3rem;
        }
    }

    .video-header {
        color: white;
    }
}

// HEADER + FOOTER

#app .navbar, #footer {
    border-radius: 0;
    background: #2a5a8a;
    z-index: 10;

    img {
        max-width: 400px;
        margin-left: -2.5rem;
        margin-right: -2.5rem;

        @include breakpoint(smallest) {
            max-width: 300px;
        }
    }

    a {
        color: white;
    }

    > .menu {
        border: none;
        display: flex;
        justify-content: center;
        align-items: center;
    }

    .header {
        text-align: center;
        color: white;
        margin-top: 0;
        font-weight: 100;
        font-size: 1.5rem;
    }

    .item > .content {
        padding: 2rem;

        @include breakpoint(tablet) {
            padding: 0rem;
            padding-bottom: 1rem;
        }
    }

    .header-burger {
        display: none;

        @include breakpoint(tablet) {
            display: block;
        }


        .item {
            font-size: 1.5rem;
            color: #2a5a8a;
            padding: 2rem;

            @include breakpoint(formobile) {
                font-size: 1.2rem;
                padding: 1.5rem;
            }
        }

        .item:hover {
            background: #2a5a8a;
            color: white;
            transition: .3s;
        }
    }

    div.header-burger {
        position: absolute;
        right: 1rem;

        .menu {
            left: -11.3rem;
            border: none;
        }

        i.icon {
            font-size: 2rem;
        }
    }

    .hide-header {
        display: flex;
        align-items: center;
        font-size: 1.2rem;

        button {
            position: absolute;
            right: 2%;
            top: 21%;
            padding: 1.5rem;
            font-size: 1.4rem;
        }

        @include breakpoint(tablet) {
            display: none;
        }
        image {
            @include breakpoint(tablet) {
                display: inline-flex;
            }
        }
    }
}

#footer {
    margin-top: 0;

    @include breakpoint(formobile) {
        padding: 2rem;

        i.icon {
            font-size: 1.5rem;
        }

        .header {
            font-size: 1.2rem;
        }

        .ui.horizontal.list > .item {
            margin-left: 0;
        }
    }
}

// HOMEPAGE

#main-background {
    background: url('/img/background1.jpg');
    height: 70vh;
    background-size: cover;
    background-position: center;
    background-position-x: -17rem;

    @include breakpoint(tablet) {
        height: 100%;
        background-position-x: 15rem;
     }

    .main-about-text {
        @include breakpoint(tablet) {
            padding: 5rem;
            background: #ffffffbd;
        }
        padding: 8rem;
        padding-top: 11rem;

        .header {
            font-size: 2.9rem;
            line-height: 3.5rem;
            widows: 2;

            @include breakpoint(smallest) {
                font-size: 3.2rem;
                line-height: normal;
            }

            @include breakpoint(formobile) {
                font-size: 2.3rem;
                line-height: normal;
            }
        }
        p {
            font-size: 1.3rem;

            @include breakpoint(smallest) {
                font-size: 1.9rem;
            }
            @include breakpoint(formobile) {
                font-size: 1.1rem;
            }
        }
    }

    #about-me-front {
        font-size: 3rem;
        font-weight: 900;
    }

    button {
        @include breakpoint(formobile) {
            font-size: 1rem;
        }
    }
}

#tutorial-videos {
    padding-left: 10rem;
    padding-right: 10rem;
    padding-bottom: 8rem;
    padding-top: 6rem;
    display: block;
    text-align: center;
    background: #2a5a8a;
    @include breakpoint(tablet) {
        padding: 4rem;
    }

    @include breakpoint(formobile) {
