    .about-vid {
        padding: 6rem;

        .header {
            font-size: 2.9rem;
            text-align: center;
            padding-bottom: 2rem;
        }

        @include breakpoint(smallest) {
            padding: 3rem;
        }

        @include breakpoint(formobile) {
            .header {
                font-size: 2.2rem;
            }
        }
    }
