        padding: 0rem;
    }

    .about-text, .about-img {
        box-shadow: 0px -1px 23px #2a5a89;
        border: 17px inset #2a5a89;
        background: #ffffff94;

        @include breakpoint(laptop) {
            width: 100% !important;
        }
        @include breakpoint(huge) {
            width: 100% !important;
        }

    }

    #about-card {
        width: 100%;
        padding: 0rem;

        /* Style only for Google Chrome/Opera */
        @media screen and (-webkit-min-device-pixel-ratio:0) {
            @include breakpoint(huge) {
                width: 50%;
                display: inline-flex;
            }

            @include breakpoint(laptop) {
                width: 50%;
                display: inline-flex;
            }
        }

        /* Style only for Mozilla Firefox */
        @-moz-document url-prefix() {
            @include breakpoint(huge) {
                width: 92%;
                display: inline-flex;
            }

            @include breakpoint(laptop) {
                width: 92%;
                display: inline-flex;
            }
        }

        @include breakpoint(smallest) {
            width: 100%;
            display: block;
        }

        @include breakpoint(formobile) {
            width: 100%;
            display: block;
        }

        img:first-child {
            border-bottom: 16px solid #2a5a89;

            @include breakpoint(huge) {
                border-bottom: none;
                border-right: 16px solid #2a5a89;
            }

            @include breakpoint(laptop) {
                border-bottom: none;
                border-right: 16px solid #2a5a89;
            }

            @include breakpoint(smallest) {
                border-bottom: 16px solid #2a5a89;
                border-right: none;
            }
        }
    }

    p:nth-child(2) {
        font-size: 1.3rem;
        font-weight: 900;

        @include breakpoint(smallest) {
            font-size: 1rem;
        }
    }

    img {
        // padding: 1rem;
        width: 100%;

        @include breakpoint(formobile) {
            border: 20px     #fff;
        }
    }

    p {
        font-size: 1.1rem;
        flex-grow: 0;
        -webkit-box-flex: 0;
        -ms-flex-positive: 0;

        @include breakpoint(smallest) {
            font-size: .9rem;
        }
    }
}

