// RESPONSIVE IFRAME

.iframe-container {
  overflow: hidden;
  padding-top: 56.25%;
  position: relative;

  @include breakpoint(tablet) {
    width: 80%;
    margin: 0 auto;
  }

  @include breakpoint(smallest) {
    width: 100%;
  }

  iframe {
    position: absolute;
    top: 0;
    left: 0;
    border: 0;
    width: 100%;
    height: 100%;
  }
} 

